import React, {useState} from "react"
import Base from "../core/Base"
import {Link} from "react-router-dom"
import { signup } from "../auth/helper";

const Signup = () =>{

    const [values, setValues] =useState({
        name: "",
        email: "",
        password: "",
        error: "",
        success: false
    });

    const {name, email, password, error, success} = values

    const handleChange = name => event => {
        setValues({...values, error: false, [name]: event.target.value})
    };

    const onSubmit = event => {
        event.preventDefault()
        setValues({...values, error: false})
        signup({name, email, password})
        .then(data => {
            if(data.error){
                setValues({...values, error: data.error, success: false})
            }else{
                setValues({
                    ...values,
                    name: "",
                    email: "",
                    password: "",
                    error: "",
                    success: true
                });
            }
        })
        .catch(console.log("Error in signup"));
    }

    const signupFrom = () =>{
        return(
            <div className="row">
                <div className="col-md-6 offset-sm-3 text-left">
                    <form action="">
                        <div className="form-group">
                            <label className="text-light">
                                Name
                            </label>
                            <input type="text" onChange={handleChange("name")} className="form-control" value={name} />
                        </div>
                        <div className="form-group">
                            <label className="text-light">
                                Email
                            </label>
                            <input className="form-control" onChange={handleChange("email")} type="email" value={email} />
                        </div>
                        <div className="form-group">
                            <label className="text-light">
                                Password
                            </label>
                            <input className="form-control" onChange={handleChange("password")} type="password" value={password} />
                        </div>
                        <button className="btn btn-success btn-block" onClick = {onSubmit}>
                            Submit
                        </button>
                    </form>
                </div>
            </div>
        )
    }

    const sucessMessage = () => {
        return(
            <div className="alert alert-danger" 
            style={{display: success ? "" : "none"}}>
                New account is created sucessfully. please <Link to="/signin">Login here</Link>
            </div>
        )}

    const errorMessage = () => {
        return(
            <div className="alert alert-danger"
            style={{display: error ? "" : "none"}}>
                {error}
            </div>
        )}
    return(
        <Base title="sign up page" description="A page foruser to sign up!">
            {sucessMessage()}
            {errorMessage()}
            {signupFrom()}
            <p className="text-white text-center">{JSON.stringify(values)}</p>
        </Base>
    );
};

export default Signup;
